mapper.select:
		SELECT 
		
		FROM
        ${subSysDatabase}.role
		
			
				AND role.id = #{id}
			
			
				AND role.code like CONCAT('%', #{code}, '%')  
			
			
				AND role.description like CONCAT('%', #{description}, '%')  
			
			
				AND role.is_deleted = #{isDeleted}
			
			
				limit #{offset}, #{pageSize}

SELECT count(1) FROM ${subSysDatabase}.role
AND role.id = #{id}
AND role.code like CONCAT('%', #{code}, '%')
AND role.description like CONCAT('%', #{description}, '%')
AND role.is_deleted = #{isDeleted}
